// LandingPage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Creative5 from '.';

const LandingPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    industry: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
    console.log('Form submitted:', formData);
  };

  const { firstName, lastName, email, industry } = formData;

  return (
    <div className="bg-cover bg-center h-screen" style={{ backgroundImage: `url(${BackgroundImage})` }}>
      <nav className="flex justify-between items-center p-4">
        <Link to="/" className="text-white text-2xl font-bold">
          Richard
        </Link>
        <div className="flex space-x-4">
          <a href="#" className="text-white">
            <i className="fab fa-facebook"></i>
          </a>
          <a href="#" className="text-white">
            <i className="fab fa-twitter"></i>
          </a>
          <a href="#" className="text-white">
            <i className="far fa-envelope"></i>
          </a>
          <a href="#" className="text-white">
            <i className="fab fa-instagram"></i>
          </a>
        </div>
      </nav>
      <hr className="border-t-2 border-black" />
      <div className="text-white p-8">
        <h1 className="text-4xl font-bold mb-4">
          Sign up now and be amongst the first to take advantage of the biggest creator marketplace in Africa.
        </h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">First Name:</label>
              <input
                type="text"
                name="firstName"
                value={firstName}
                onChange={handleChange}
                required
                className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
                placeholder="John"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Last Name:</label>
              <input
                type="text"
                name="lastName"
                value={lastName}
                onChange={handleChange}
                required
                className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
                placeholder="Doe"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Industry:</label>
            <input
              type="text"
              name="industry"
              value={industry}
              onChange={handleChange}
              required
              className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
              placeholder="Niche"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Email:</label>
            <input
              type="email"
              name="email"
              value={email}
              onChange={handleChange}
              required
              className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
              placeholder="john.doe@example.com"
            />
          </div>
          <button
            type="submit"
            className="bg-indigo-900 text-white p-2 rounded-md hover:bg-indigo-600 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
          >
            Join Waitlist
          </button>
        </form>
      </div>
    </div>
  );
};

export default LandingPage;
