import React, { useState } from 'react';

const LandingPage = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    industry: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Call the onSubmit prop with the form data
    onSubmit(formData);
  };

  const { firstName, lastName, email, industry } = formData;

  return (
    <div className="bg-indigo-500 min-h-screen flex flex-col">
      <div>

        <img src="" alt="creative person" />
      </div>

      <hr className="my-4 border-black" />

      <div className="container mx-auto my-8 flex-grow">
        <div className="text-center mb-8">
          <p className="text-6xl text-gray-800">
          Not rewarded enough ​for your creativity?
          </p>
          <p className='text-gray-800'>Sign up now and be amongst the first the take ​advantage of the biggest creator marketplace in ​Africa.</p>
        </div>

        <div className="mx-auto max-w-md">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">First Name:</label>
                <input
                  type="text"
                  name="firstName"
                  value={firstName}
                  onChange={handleChange}
                  required
                  className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
                  placeholder="John"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Last Name:</label>
                <input
                  type="text"
                  name="lastName"
                  value={lastName}
                  onChange={handleChange}
                  required
                  className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
                  placeholder="Doe"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">Industry:</label>
              <input
                type="text"
                name="industry"
                value={industry}
                onChange={handleChange}
                required
                className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
                placeholder="Niche"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">Email:</label>
              <input
                type="email"
                name="email"
                value={email}
                onChange={handleChange}
                required
                className="mt-1 p-2 block w-full rounded-md border-gray-300 focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
                placeholder="john.doe@example.com"
              />
            </div>
            <button
              type="submit"
              className="bg-indigo-900 text-white p-2 rounded-md hover:bg-indigo-600 focus:ring focus:ring-indigo-200 focus:ring-opacity-50 focus:outline-none"
            >
              Join Waitlist
            </button>
          </form>
        </div>
      </div>
      <footer className='bg-black text-white p-4'>
        <small>Terms & Support</small>
      </footer>
    </div>
  );
};

export default LandingPage;
